import { Container, Table, Row } from 'react-bootstrap';
import ModeloProduto from '../Components/Produtos';
import '../Components/Produtos/Produtos.css';
import { useState, useEffect } from 'react';

export default function Controle(props) {
    const [ controle, setControle ] = useState([]);

    useEffect(async () => {
        const resposta = await fetch("http://localhost/tchegames_React/src/php/controle.php");
        const dados = await resposta.json();
        setControle(dados);   
        console.log(dados);     
    }, []);

    return (
        <Container>
            <Row>
            {controle && controle.map(item => <ModeloProduto imagem={item.imagem} descricao={item.descricao} precoCheio={item.precoCheio} precoDesconto={item.precoDesconto} categoria={item.categoria} />)}
            </Row>
        </Container>
    )

        return (
       <Container>
       <h4>Controle de pedidos</h4><br />

        <Table striped bordered hover responsive="sm" variant="dark">
            <thead>
                <tr>
                <th>Id_Pedidos</th>
                <th>Data</th>
                <th>Cliente</th>
                <th>Email</th>
                <th>Telefone</th>
                <th>Endereço</th>
                <th>Id_Produto</th>
                <th>Quantidade (Pedido)</th>
                <th>Categoria</th>
                <th>Descrição</th>
                <th>Preço Cheio</th>
                <th>Preco Desconto</th>
                </tr>
            </thead>
            <tbody>
                <tr>
                    <td>{controle && controle.map(item => item.id_pedidos)}</td>
                    <td>{controle && controle.map(item => item.data)}</td>
                    <td>{controle && controle.map(item => item.nomeClientes)}</td>
                    <td>{controle && controle.map(item => item.email)}</td>
                    <td>{controle && controle.map(item => item.telefone)}</td>
                    <td>{controle && controle.map(item => item.endereco)}</td>
                    <td>{controle && controle.map(item => item.produto_id)}</td>
                    <td>{controle && controle.map(item => item.quantidade)}</td>
                    <td>{controle && controle.map(item => item.categoria)}</td>
                    <td>{controle && controle.map(item => item.descricao)}</td>
                    <td>{controle && controle.map(item => item.precoCheio)}</td>
                    <td>{controle && controle.map(item => item.precoDesconto)}</td>
                </tr>
            </tbody>            
        </Table>
        </Container>    
    );
}
