import { Container, Row, Nav, Col } from 'react-bootstrap';
import ModeloProduto from '../Components/Produtos';
import Acessorios from './Acessorios';
import '../Components/Produtos/Produtos.css';
import { useState, useEffect } from 'react';


export default function Produtos() {
    const [ produtos, setProdutos ] = useState([]);

    useEffect(async () => {
        const resposta = await fetch("http://localhost/tchegames_React/src/php/produtos.php");
        const dados = await resposta.json();
        setProdutos(dados);  
              
    }, []);

    return (
        <Row>
            <Col sm={2} id="largura">
            <Nav className="d-none d-md-block bg-light sidebar" id="lateral">
            <Nav.Item onclick="exibirTodos()">Todos (15)
            <Nav.Link href="./Acessorios">Active</Nav.Link>
            </Nav.Item>
            <Nav.Item onclick="exibirCategoria('consoles')">Consoles (6)</Nav.Item>
            <Nav.Item onclick="exibirCategoria('jogos')">Jogos (5)</Nav.Item>
            <Nav.Item onclick="exibirCategoria('acessorios')">Acessórios (4)</Nav.Item>
            </Nav>
            </Col>
            
            <Col sm={10}>
            <h3>Nossos produtos</h3>
            <br /><br />
            
            {produtos && produtos.map(item => <ModeloProduto imagem={item.imagem} descricao={item.descricao} precoCheio={item.precoCheio} precoDesconto={item.precoDesconto} categoria={item.categoria} />)}
            </Col>
            </Row>
        
    )
}
