import {useState} from 'react';
import { Form, Button, Row } from "react-bootstrap";


export default function Pedidos(props) {

    const [ form, setForm ] = useState({
        nomeClientes: "",
        email:"",
        telefone: "",
        endereco: "",
        produto_id: "",
        quantidade: ""
    });

    const alteracao = (evento) => {
        setForm({
            ...form,
            [evento.target.id]: evento.target.value
        })        
    };  

    const Envio = async (evento) => {
        evento.preventDefault();
                
        const resultado = fetch("http://localhost/tchegames_React/src/php/pedidos.php", { method: "POST", body: new FormData(evento.target) });
    };   
    

    

    return (
        <div className="form-group col-md-6">            
                <h4>Realizar pedido</h4>
                <Form onSubmit={Envio}>
                <Form.Group>
                    <Form.Label>Nome:</Form.Label>
                    <Form.Control onChange={alteracao} type="text" id="nomeClientes" name="nomeClientes" placeholder="Nome completo" />
                   
                    <Form.Label>Email:</Form.Label>
                    <Form.Control onChange={alteracao} type="email" id="email" name="email" placeholder="Email" />
                   
                    <Form.Label>Telefone:</Form.Label>
                    <Form.Control onChange={alteracao} type="text" id="telefone" nome="telefone" placeholder="Telefone" />

                    <Form.Label>Endereço:</Form.Label>
                    <Form.Control onChange={alteracao} type="text" id="endereco" name="endereco" placeholder="Endereço completo" />

                    <Form.Label>Selecione o produto desejado:</Form.Label>
                    <Form.Control as="select" id="produto_id" name="produto_id" placeholder="Nome do produto">
                    <option value="1">Nintendo Switch - Vermelho e Azul</option>
                    <option value="2">Nintendo Switch - Cinza</option>
                    <option value="">Nintendo Switch Lite - Amarelo</option>
                    <option value="">Nintendo Switch Lite - Cinza</option>
                    <option value="">Nintendo Switch Lite - Coral</option>
                    <option value="">Nintendo Switch Lite - Turquesa</option>
                    <option value="">Super Mario Odyssey</option>
                    <option value="">Mario Kart</option>
                    <option value="">The Legend Of Zelda Breath Of The Wild</option>
                    <option value="">The Legend Of Zelda Breath Of The Wild</option>
                    <option value=""></option>
                    <option value=""></option>
                    <option value=""></option>
                    <option value=""></option>
                    <option value=""></option>
                    </Form.Control>


                    <Form.Label>Quantidade:</Form.Label>
                    <Form.Control onChange={alteracao} type="text" id="quantidade" name="quantidade" placeholder="Quantidade do produto" />

                    <Button variant="info" type="submit" onclick="alert('Pedido enviado com sucesso')">Enviar</Button>
                </Form.Group>
                </Form>          
        </div>
    )
}
