import {useState} from 'react';
import { Form, Button, Row } from "react-bootstrap";


export default function Pedidos() {
    const [ form, setForm ] = useState({
        nomeCliente: "",
        email:"",
        telefone: "",
        endereco: "",
        produto: "",
        quantidade: ""
    });

    const alteracao = (evento) => {
        setForm({
            ...form,
            [evento.target.id]: evento.target.value
        })        
    };  

    return (
        <div class="form-group col-md-6">            
                <h4>Realizar pedido</h4>
                <Form>
                <Form.Group>
                    <Form.Label>Nome:</Form.Label>
                    <Form.Control onChange={alteracao} type="text" id="nomeClientes" placeholder="Nome completo" />
                   
                    <Form.Label>Email:</Form.Label>
                    <Form.Control onChange={alteracao} type="email" id="email" placeholder="Email" />
                   
                    <Form.Label>Telefone:</Form.Label>
                    <Form.Control onChange={alteracao} type="text" id="telefone" placeholder="Telefone" />

                    <Form.Label>Endereço:</Form.Label>
                    <Form.Control onChange={alteracao} type="text" id="endereco" placeholder="Endereço completo" />

                    <Form.Label>Nome do produto:</Form.Label>
                    <Form.Control onChange={alteracao} type="text" id="produto" placeholder="Nome do produto" />

                    <Form.Label>Quantidade:</Form.Label>
                    <Form.Control onChange={alteracao} type="text" id="quantidade" placeholder="Quantidade" />

                    <Button variant="info" type="submit">Enviar</Button>
                </Form.Group>
                </Form>          
        </div>
    )
}
