function ModeloProduto(props) {
    return (

        function show_info(event){
            const target = event.currentTarget;
            const elemento = target.getElementsByTagName("figcaption")[0];
            elemento.style.visibility = "initial";

        },

        <figure className={props.categoria}>
            <img src={require('./img/produtos/Acessorios/Organizador Jogos.jpg').default} alt="teste" />
            <figcaption>
                <p className="nome-prod">
                    {props.nome}
                </p>
                <p className="precofinal-prod">
                    R$ {props.preco}
                </p>
            </figcaption>
        </figure>
    )
}

export default ModeloProduto;
